mapper.update:
UPDATE member SET
	nickname=#{nickname}, email=#{email}, password=#{password}, 
	tel=#{tel}, editdate=now(), sms=#{sms}, shopaddress=#{shopaddress}, 
	intro=#{intro}
WHERE membno=#{membno}

UPDATE member SET
	password=#{password}
WHERE membno=#{membno}

mapper.select:
SELECT membno, shopaddress
FROM member
WHERE shopaddress=#{shopaddress}

SELECT membno, nickname, email, tel, DATE_FORMAT(joindate, '%Y-%m-%d') AS joindate, DATE_FORMAT(editdate, '%Y-%m-%d') AS editdate, outmember, rate, (select count(r.prodno) from record r left join product p on p.prodno=r.prodno where r.tradecon='W' and r.refund='N' and r.buyer=membno) as totalamount, (select sum(p.price) from record r left join product p on p.prodno=r.prodno where r.tradecon='W' and r.refund='N' and r.buyer=membno) as totalpay FROM member
outmember=#{outmember}
(outmember='y' OR outmember='a')
AND (email LIKE concat('%', #{email}, '%') or nickname LIKE concat('%', #{email}, '%'))
ORDER BY membno DESC
LIMIT #{offset}, #{listCount}

SELECT membno, nickname, email, tel, DATE_FORMAT(joindate, '%Y-%m-%d') AS joindate, DATE_FORMAT(editdate, '%Y-%m-%d') AS editdate, outmember, rate, (select count(r.prodno) from record r left join product p on p.prodno=r.prodno where r.tradecon='W' and r.refund='N' and r.buyer=membno) as totalamount, (select sum(p.price) from record r left join product p on p.prodno=r.prodno where r.tradecon='W' and r.refund='N' and r.buyer=membno) as totalpay FROM member
outmember=#{outmember}
(outmember='y' OR outmember='a')
AND (email LIKE concat('%', #{email}, '%') or nickname LIKE concat('%', #{email}, '%'))
ORDER BY totalamount ASC
LIMIT #{offset}, #{listCount}

