mapper.select:
SELECT msgno, receiver, sender, m.nickname as senderNickname, content, receiversave, p.prodno, DATE_FORMAT(ms.regdate, '%Y-%m-%d %H:%i:%S') AS regdate, p.subject as subject FROM msgbox ms INNER JOIN member m ON ms.sender=m.membno INNER JOIN product p ON ms.prodno=p.prodno WHERE receiver=#{receiver} AND receiverdelete IS NULL
ORDER BY ms.regdate DESC
LIMIT #{offset}, #{listCount}

mapper.insert:
INSERT INTO msgbox (
	sender, receiver, content, prodno, regdate
) VALUES (
	#{sender}, #{receiver}, #{content}, #{prodno}, now()
	)

mapper.update:
UPDATE msgbox SET
	receiversave='Y'
WHERE msgno=#{msgno}

UPDATE msgbox SET
	receiverdelete='Y'
WHERE msgno=#{msgno}
